import React, {PropTypes, Component} from 'react';

const panleRadius = 30;

const styles = {
  container: {
    textAlign: 'center',
    position: 'relative',
    width: '180px',
    height: '180px',
    margin: '0px auto 0px auto',
    userSelect: 'none'
  },
  canvas: {
    position: 'absolute',
    left: '50%',
    marginLeft: '-90px',
    borderRadius: '50%',
    boxShadow: '0px 2px 5px 0px rgba(0, 0, 0, 0.12), 0px 2px 10px 0px rgba(0, 0, 0, 0.08)'
  },
  panle: {
    borderRadius: '50%',
    width: panleRadius*2 + 'px',
    height: panleRadius*2 + 'px',
    position: 'absolute',
    left: '50%',
    top: '50%',
    marginLeft: '-27px',
    marginTop: '-27px',
    boxShadow: '0px 2px 5px 0px rgba(0, 0, 0, 0.12), 0px 2px 10px 0px rgba(0, 0, 0, 0.08)',
    textAlign: 'center'
  },
  icon: {
    display: 'block',
    width: '62px',
    height: '62px',
    left: '50%',
    top: '50%',
    position: 'absolute',
    marginLeft: '-31px',
    marginTop: '-31px',
    backgroundColor: '#000',
    borderRadius: '50%',
    padding: '10px',
    boxSizing: 'border-box',
    transition: 'all 0.1s ease-in-out 0s'
  }
};

//色卡
const colorImage = "data:image/png;base64,";
const lightImg = require('./ic_lamp.png');

class ColorPick extends Component {
  constructor(props) {
    super(props);
    this.onmousedown = this
      .onmousedown
      .bind(this);
    this.onmousemove = this
      .onmousemove
      .bind(this);
    this.onmouseup = this
      .onmouseup
      .bind(this);
    this.getColor = this
      .getColor
      .bind(this);

    this.state = {
      color: props.color
    };
    this.clickState = false;

    //判断点击事件类型
    this.hasTouch = 'ontouchstart' in window,
    this.startEvent = this.hasTouch ? 'touchstart' : 'mousedown';
    this.moveEvent = this.hasTouch ? 'touchmove' : 'mousemove';
    this.endEvent = this.hasTouch ? 'touchend' : 'mouseup';
  }

  componentWillReceiveProps(nextProps) {
    if(nextProps.color != this.props.color){
      this.setState({
        color: nextProps.color
      });
    }
  }

  //处理一个数组，数组内容是rgb颜色，把它转换成文字
  getColor(c) {
    const red = c[0];
    const green = c[1];
    const blue = c[2];
    const color = 'rgb(' + red + ',' + green + ',' + blue + ')';
    return color;
  }

  onmousedown(event) {
    //兼容事件
    const e = this.hasTouch ? event.touches[0] : event;
    console.log(event);
    //阻止冒泡
    event.stopPropagation();
    event.preventDefault();

    this.clickState = true;
    //获取原始坐标
    const originalPosition = {
      x: e.pageX,
      y: e.pageY
    };
    //距离window的坐标
    let position = this.windowToCanvas(this.canvas, e.pageX, e.pageY);
    //isCrossing的值代表是否命中色卡
    const isCrossing = this.crossingCorrect(position.x, position.y, this.props.radius, this.props.radius, this.props.radius);
    if(isCrossing){
      //触发回调，这里写这个回调是为了给开发者处理一些特殊的问题
      this.props.onMouseDownIsHit(false, originalPosition);
      return;
    }else{
      this.props.onMouseDownIsHit(true, originalPosition);
    }
    const c = this.ctx.getImageData(position.x, position.y, 1, 1).data;
    const color = this.getColor(c);
    this.props.onChange(color);
  }

  onmousemove(event) {
    event.stopPropagation();
    event.preventDefault();
    if (this.clickState) {
      //兼容事件
      const e = this.hasTouch ? event.touches[0] : event;
      let position = this.windowToCanvas(this.canvas, e.pageX, e.pageY);
      const isCrossing = this.crossingCorrect(position.x, position.y, this.props.radius, this.props.radius, this.props.radius);
      if(isCrossing){
        return;
      }
      const c = this.ctx.getImageData(position.x, position.y, 1, 1).data;
      const color = this.getColor(c);
      this.props.onChange(color);
    }
  }

  //判断是否命中色卡
  crossingCorrect(x, y, x1, y1, radius) {
    //x是事件触发点  x1是圆心
    const sideX = x1 - x;
    const sideY = y1 - y;
    //求半径
    const pointRadius = Math.sqrt(Math.pow(sideX, 2) + Math.pow(sideY, 2));

    if(pointRadius > radius){
      return true;
    }else{
      return false;
    }
  }

  onmouseup(event) {
    event.stopPropagation();
    event.preventDefault();
    if (this.clickState) {
      this.clickState = false;
      //触发回调
      this.props.onChangeEnd(this.state.color);
    }
  }

  //计算距离window的位置
  windowToCanvas(canvas, x, y) {
    const bbox = canvas.getBoundingClientRect();
    const positionX = x - bbox.left * (canvas.width / bbox.width);
    const positionY = y - bbox.top * (canvas.height / bbox.height);
    return {x: positionX, y: positionY};
  }

  componentDidMount() {
    const image = new Image();
    image.onload = () => {
      //渲染色卡
      this.ctx = this.canvas.getContext('2d');
      this.ctx.drawImage(image, 0, 0, this.props.radius*2, this.props.radius*2);
    };
    image.src = colorImage;

    //判断事件类型 添加监听
    this.canvas.addEventListener(this.startEvent, this.onmousedown);
    this.canvas.addEventListener(this.moveEvent, this.onmousemove);
    this.canvas.addEventListener(this.endEvent, this.onmouseup);

  }

  componentWillUnmount() {
    //销毁
    this.canvas.removeEventListener(this.moveEvent, this.onmousemove);
    this.canvas.removeEventListener(this.endEvent, this.onmouseup);
  }

  onHandleClick = () => {
    const {disable} = this.props;
    if(disable && this.props.onIconClick) {
      this.props.onIconClick();
    }
  }

  render() {
    const {disable, radius, color} = this.props;

    const proportion = radius/panleRadius;
    const panleDisable = {
      width: radius*2+'px',
      height: radius*2+'px',
      position: 'absolute',
      padding: '30px',
      marginLeft: -radius + 'px',
      marginTop: -radius + 'px',
    };
    console.log(panleDisable);
    
    return (
      <div style={Object.assign({}, styles.container, {width: radius*2, height: radius*2})}>
        <canvas
          style={Object.assign({}, styles.canvas, {marginLeft: -this.props.radius+'px'})}
          ref={c => {
          this.canvas = c;
        }}
          width={radius*2}
          height={radius*2}></canvas>

        {/*<div
          style={Object.assign({}, 
            styles.panle, 
            {backgroundColor: this.state.color},
            )}>
        </div>
        <img style={Object.assign({}, styles.icon)} src={lightImg}/>*/}
        <svg onClick={this.onHandleClick} style={Object.assign({}, styles.icon, disable? panleDisable: {})} viewBox="0 0 24 24">
          <path style={{fill: color}} fill="#000000" d="M12,6A6,6 0 0,1 18,12C18,14.22 16.79,16.16 15,17.2V19A1,1 0 0,1 14,20H10A1,1 0 0,1 9,19V17.2C7.21,16.16 6,14.22 6,12A6,6 0 0,1 12,6M14,21V22A1,1 0 0,1 13,23H11A1,1 0 0,1 10,22V21H14M20,11H23V13H20V11M1,11H4V13H1V11M13,1V4H11V1H13M4.92,3.5L7.05,5.64L5.63,7.05L3.5,4.93L4.92,3.5M16.95,5.63L19.07,3.5L20.5,4.93L18.37,7.05L16.95,5.63Z" />
      </svg>
      </div>
    );
  }
}

ColorPick.propTypes = {
  onMouseDownIsHit: PropTypes.func,
  onChange: PropTypes.func,
  onChangeEnd: PropTypes.func,
  radius: PropTypes.number,
  color: PropTypes.string,
  disable: PropTypes.bool
};

ColorPick.defaultProps = {
  onMouseDownIsHit: () => {},
  onChange: () => {},
  onChangeEnd: () => {},
  radius: 90,
  color: 'rgb(0, 227, 255)',
  disable: false
};

export default ColorPick;
